create table task (due_date date, priority integer not null, created_date datetime(6) not null, id bigint not null auto_increment, last_modified_date datetime(6), created_by varchar(255) not null, description varchar(255), last_modified_by varchar(255), status varchar(255), title varchar(255), primary key (id));
create table task_history (due_date date, id integer not null auto_increment, priority integer not null, created_date datetime(6) not null, last_modified_date datetime(6), task_id bigint not null, created_by varchar(255) not null, description varchar(255), last_modified_by varchar(255), status varchar(255), title varchar(255), action enum ('INSERTED','UPDATED','DELETED'), primary key (id));
